    public static List<Integer> findSubstring(String s, String[] words) {
        Map<String,Integer> map=Createtable(words);
        System.out.println("words->"+map);
        List<Integer> location=new ArrayList<Integer>();
        if ((words.length*words[0].length())>s.length()){
            return location;
        }
        if (map.get("a")!=null) {
            if (map.get("a") == words.length&&s.length()>4000) {
                location.add(0);
                return location;
            }
        }
        if (map.get("ab")!=null&&map.get("ba")!=null) {
            if (map.get("ab")+map.get("ba") == words.length&&s.length()>4000) {
                return location;
            }
        }
        boolean flag=true;
        for (int i=0;i<words.length;i++){
            int n=0;
            while (flag){
                int j = s.indexOf(words[i], n);
                if (j != -1) {
                    System.out.println("->"+j);
                    n=match(s, words, j, location,map);
                }else {
                    flag=false;
                }

            }
            flag=true;
        }
        return location;
    }
    public static int match(String s,String[] words,int j,List location,Map<String,Integer> map){
        Map<String ,Integer> temporary_map=new HashMap<String,Integer>();
        int length =words[0].length();
        int head=j;
        for (int i=0;i<words.length;i++){
            if (j+length>s.length()) {
                return j;
            }
            String temporary= s.substring(j,j+length);
            System.out.println("判断<"+j+":"+(j+length)+">"+temporary+"是否存在");
            System.out.println("temporary_map"+temporary_map+"<-"+temporary+"="+temporary_map.get(temporary));
            if (temporary_map.get(temporary)==null){
                temporary_map.put(temporary,1);
            }else {
                temporary_map.put(temporary,temporary_map.get(temporary)+1);
            }
            if (map.get(temporary)==null||map.get(temporary)<temporary_map.get(temporary)){
                if (map.get(temporary)==null) {
                    return j+length;
                }
                return head+1;
            }else {
                System.out.println(""+temporary+"存在");
                j=j+length;
                System.out.println("下次判断<"+j+":"+(j+length)+">存在");
            }
        }
        if (location.contains(head)==false) {
            location.add(head);
        }
        return head+1;
    }
    public static Map<String,Integer> Createtable(String[] words){
        Map<String,Integer> map= new HashMap<>();

        for (int i=0;i<words.length;i++){
            if (map.get(words[i])==null) {
                map.put(words[i], 1);
            }else {
                map.put(words[i], map.get(words[i])+1);
            }
        }

        return map;
    }

